bool findele(vector<vector<char>>& board, string word, int i, int j, int index){
    int check = word.length();
    //cout << index << endl;
    if (index == check){
        //cout << "found" << endl;
        return true;
    }
    if(i<0 or j<0 or i>=board.size() or j>=board[0].size())
        return false;
    bool fin = false;
    if(board[i][j] == word[index]){
        char tmp = board[i][j];
        board[i][j] = '*';
        //cout << "\nfound " << board[i][j];
        fin = findele(board, word, i+1, j, index+1) or findele(board, word, i-1, j, index+1) or findele(board, word, i, j+1, index+1) or findele(board, word, i, j-1, index+1); 
        board[i][j] = tmp;
    }
    return fin;
}
    bool exist(vector<vector<char>>& board, string word) {
        //int check =  word.length();
        vector<vector<int>> visited(board.size(), vector<int> (board[0].size(), false));
        for(int i = 0;i<board.size();i++){
            for(int j = 0;j<board[0].size();j++){
                if(board[i][j] == word[0] and findele(board, word, i, j, 0))
                    return true;
            }
        }
        return false;
    }

